import React, { useState } from "react";
import Dropdown from "./Dropdown";
import { styled } from "styled-components";
const DDiv = styled.div`
width: 890px;
height: 240px;
background-color: white;

`;
const Div = styled.div`
display: flex;
align-items: center;
width: 850px;
margin: 0 auto;

`;
const Header5 = styled.p`
color: var(--text-black, #111);
margin-right: 27px;
/* Admin/Body/B1-16 R */
font-family: 'NotoSansKR';
font-size: 16px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 22.4px */
margin-top: 15px;
`;
const Header6 = styled.p`
color: var(--text-black, #111);
margin-right: 17px;
/* Admin/Body/B1-16 R */
font-family: 'NotoSansKR';
font-size: 16px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 22.4px */
margin-top: 15px;
`;
const Search = styled.input`
width: 324px;
height: 41px;
flex-shrink: 0;
border-radius: 2px;
border: 1px solid var(--admin-gray-stroke, #E8E8E8);
background: var(--white, #FFF);
margin-left: 5px;
font-family: "NotoSansKR";
&:focus{
 outline: none;
 &::placeholder {
    color: transparent; /* 기본 placeholder 숨김 */
  }
}

`;
const RadioContainer = styled.div`
  display: flex;
  align-items: center;
`;

const Hr = styled.hr`
  width: 95%;
margin: 15px auto;
`;
const RadioInput = styled.input`
margin-right: 5px;
margin-left: 10px;
`;
const RadioLabel = styled.label`
color: #000;
/* Admin/Body/B2-15 R */
font-family: "NotoSansKR";
font-size: 15px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 21px */
`;
const Datep = styled.p`
width: 85px;
color: var(--text-black, #111);
/* Admin/Body/B1-16 R */
font-family: 'NotoSansKR';
font-size: 16px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 22.4px */
margin-right: 5px;
height: 100%;
margin-top: 15px;
`;

const DateButton = styled.button`
  background-color: ${(props) => (props.clicked ? '#0079E0' : 'white')};
  color:${(props) => (props.clicked? 'white':'black')};
  border: ${(props) => (props.clicked? 'none':'1px solid var(--admin-gray-stroke, #E8E8E8)' )};
  width: 70px;
height: 35px;
/* padding: 6px 23px 6px 23px; */
border-radius: 5px;
gap: 10px;
margin-right: 5px;
  font-family: "NotoSansKR";
`;
const DateInput = styled.input`
width: 133px;
height: 35px;
border-radius: 2px;
border: 2px solid var(--admin-gray-stroke, #E8E8E8);
margin-left: 23px;
  font-family: "NotoSansKR";
`;
const ButtonContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;
const SearchButton = styled.button`
  background-color:#ffffff;
  width: 70px;
height: 35px;
/* padding: 6px 23px 6px 23px; */
border-radius: 5px;
  border: 1px solid var(--admin-gray-stroke, #E8E8E8);

margin-right: 5px;

&:active {
    background-color: #0079E0;
    transform: scale(0.95);
    border: none;
  }
    font-family: "NotoSansKR";
`;

export default function SearchRequire(){
    const dropdownOptions = ['제목만', '본문만', '제목+본문', '작성자','답변자'];
    const checkBoxList = ['전체', '답변대기', '진행 중', '답변완료'];
    const [dateButtonClicked, setDateButtonClicked] = useState([false, false, false, false, false, false]);
    const [selectedStatus, setSelectedStatus] = useState(checkBoxList[0]);
    const handleStatusChange = (status) => {
        setSelectedStatus(status);
      };
      const handleDateButtonClick = (index) => {
        const updatedDateButtonClicked = dateButtonClicked.map((item, i) => i === index);
        setDateButtonClicked(updatedDateButtonClicked);

      };

    return(
<>
<DDiv>
    <Div>
        <Header5>조건 검색</Header5>
        <Dropdown options={dropdownOptions}></Dropdown>
        <Search placeholder="검색어" class ="hide"></Search>
    </Div>
    <Div>
        <Header6>처리 상태</Header6>
        {checkBoxList.map((item, idx) => (
          <RadioContainer key={idx}>
            <RadioInput
              type="radio"
              id={item}
              name="status"
              value={item}
              checked={selectedStatus === item}
              onChange={() => handleStatusChange(item)}
            />
            <RadioLabel htmlFor={item}>{item}</RadioLabel>
          </RadioContainer>
        ))}
    </Div>
    <Div>
    <Div>
              <Datep>검색기간</Datep>
              <DateButton clicked={dateButtonClicked[0]} onClick={() => handleDateButtonClick(0)}>
                오늘
              </DateButton>
              <DateButton clicked={dateButtonClicked[1]} onClick={() => handleDateButtonClick(1)}>
                1주일
              </DateButton>
              <DateButton clicked={dateButtonClicked[2]} onClick={() => handleDateButtonClick(2)}>
                1개월
              </DateButton>
              <DateButton clicked={dateButtonClicked[3]} onClick={() => handleDateButtonClick(3)}>
                3개월
              </DateButton>
              <DateButton clicked={dateButtonClicked[4]} onClick={() => handleDateButtonClick(4)}>
                6개월
              </DateButton>
              <DateButton clicked={dateButtonClicked[5]} onClick={() => handleDateButtonClick(5)}>
                1년
              </DateButton>
              <DateInput type="date" id="Sdate" defaultValue="yyyy-mm-dd"  />
              <DateInput type="date" id="Ddate" defaultValue="yyyy-mm-dd"  />
            </Div>

    </Div>
          <Hr/>
    <ButtonContainer>
      <SearchButton>검색</SearchButton>
      <SearchButton>초기화</SearchButton>
    </ButtonContainer>
    </DDiv>
</>
    )
}
