import React from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import { CKEditor } from "@ckeditor/ckeditor5-react";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";
import { useDropzone } from "react-dropzone";
import AWS from "aws-sdk";
import axios from "axios";
import CancelImg from "../../Assets/Img/Cancel.svg";

AWS.config.update({
  accessKeyId: process.env.REACT_APP_S3_ACCESS_KEY,
  secretAccessKey: process.env.REACT_APP_S3_SECRET_ACCESS_KEY,
  region: process.env.REACT_APP_S3_REGION,
});

function DataRegister() {
  const [content, setContent] = useState("");
  const [files, setFiles] = useState([]);
  const [title, setTitle] = useState("");
  const [selectedCategory, setSelectedCategory] = useState(1); // Default to 1 (총학생회)
  const [pin, setPin] = useState(false);
  const navigate = useNavigate();
  const s3 = new AWS.S3();

  const handleFileRemove = (fileToRemove) => {
    const updatedFiles = files.filter((file) => file !== fileToRemove);
    setFiles(updatedFiles);
  };

  const handleCancelClick = () => {
    const shouldCancel = window.confirm(
      "자료 등록을 취소하시겠습니까?\n취소시 작성 중인 내용이 사라집니다."
    );
    if (shouldCancel) {
      console.log("Cancel clicked!");
      navigate("/dataCollection/studentCouncil");
    }
  };

  const handleRegisterClick = async () => {
    const shouldRegister = window.confirm(
      "해당 자료를 등록하시겠습니까?\n자료집 리스트에서 내용 수정 가능합니다."
    );

    if (shouldRegister) {
      try {
        const uploadedFileURLs = await uploadToS3();

        const data = {
          "userId": 8,
          "fileTitle": ["file1.txt", "file2.pdf"],
          "fileURL": [
            "https://example.com/files/file1.txt",
            "https://example.com/files/file2.pdf"
          ],
          "category": 1,
          "pin": true,
          "title": "Sample Title",
          "content": "This is the content of the post."
        };

        const response = await axios.post(
          "http://54.180.86.22:8080/api/shareNotices",
          data,
          {
            headers: { Authorization: localStorage.getItem("token") },
          }
        );

        console.log("API Response:", response.data);
        navigate("/dataCollection/studentCouncil");
      } catch (error) {
        alert("로그인이 필요합니다.");
        console.error("Error in handleRegisterClick:", error);
        if (error.response) {
          console.error("Response Data:", error.response.data);
        }
        throw error;
      }
    }
  };

  const onDrop = (acceptedFiles) => {
    setFiles([...files, ...acceptedFiles]);
  };

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
    onProgress: (file, progress) => {
      setFiles((prevFiles) =>
        prevFiles.map((prevFile) =>
          prevFile === file ? { ...prevFile, progress: progress } : prevFile
        )
      );
    },
  });

  const formatFileSize = (bytes) => {
    const sizes = ["Bytes", "KB", "MB", "GB", "TB"];
    if (bytes === 0) return "0 Byte";
    const i = parseInt(Math.floor(Math.log(bytes) / Math.log(1024)));
    return `${(bytes / Math.pow(1024, i)).toFixed(2)} ${sizes[i]}`;
  };

  const uploadToS3 = async () => {
    try {
      const uploadedFileURLs = [];

      for (const file of files) {
        const params = {
          Bucket: process.env.REACT_APP_S3_BUCKET, // Specify the S3 bucket name
          Key: "upload/" + `${Date.now()}.${file.name}`,
          Body: file,
        };

        const uploadResult = await s3.upload(params).promise();
        uploadedFileURLs.push(uploadResult.Location);
      }

      return uploadedFileURLs;
    } catch (error) {
      console.error("Error uploading files to S3:", error);
      throw error;
    }
  };

  return (
    <Div>
      <h2>총학생회 - 자료 등록하기</h2>
      <RegisterDiv>
        <RowDiv>
          <Title>
            제목 <RedAsterisk>*</RedAsterisk>
          </Title>
          <TextInput
            type="text"
            placeholder="제목입력"
            onChange={(e) => setTitle(e.target.value)}
          />
        </RowDiv>
        <RowDiv>
          <Writer>작성자</Writer>
          <GapDiv>
            <input
              type="radio"
              id="1"
              name="category"
              checked={selectedCategory === 1}
              onChange={() => setSelectedCategory(1)}
            />
            <label htmlFor="1">총학생회</label>
            <Space />
            <input
              type="radio"
              id="2"
              name="category"
              checked={selectedCategory === 2}
              onChange={() => setSelectedCategory(2)}
            />
            <label htmlFor="2">총동연</label>
            <Space />
            <input
              type="radio"
              id="3"
              name="category"
              checked={selectedCategory === 3}
              onChange={() => setSelectedCategory(3)}
            />
            <label htmlFor="3">중선관위</label>
          </GapDiv>
        </RowDiv>
        <RowDiv>
          <Title>첨부파일</Title>
          <FilePutDiv>
            <DropzoneDiv {...getRootProps()}>
              <input {...getInputProps()} />
              <>
                <Caption2>첨부파일을 드래그해서 업로드하세요</Caption2>
                <FileInputLabel>내 PC</FileInputLabel>
                {files.length > 0 && (
                  <UploadedFiles>
                    <ul>
                      {files.map((file, index) => (
                        <li key={index}>
                          <CancelButton
                            onClick={() => handleFileRemove(file)}
                          />
                          {file.name} - {formatFileSize(file.size)}
                        </li>
                      ))}
                    </ul>
                  </UploadedFiles>
                )}
              </>
            </DropzoneDiv>
          </FilePutDiv>
        </RowDiv>
        <RowDiv>
          <label htmlFor="pin">고정여부</label>
          <input
            type="checkbox"
            id="pin"
            checked={pin}
            onChange={(e) => setPin(e.target.checked)}
          />
        </RowDiv>
      </RegisterDiv>
      <TextEditor>
        <CKEditor
          editor={ClassicEditor}
          data={content}
          onChange={(event, editor) => setContent(editor.getData())}
          onReady={(editor) => {
            console.log("Editor is ready to use!", editor);
          }}
          onBlur={(event, editor) => {
            console.log("Blur.", editor);
          }}
          onFocus={(event, editor) => {
            console.log("Focus.", editor);
          }}
          config={{
            placeholder: "내용을 입력하세요.",
          }}
        />
      </TextEditor>
      <Button>
          <Cancel onClick={handleCancelClick}>취소</Cancel>
          <Submit onClick={handleRegisterClick}>등록하기</Submit>
        </Button>
    </Div>
  );
}

