import React from "react";
import {
  useTable,
  useSortBy,
  useFilters,
  usePagination,
  useRowSelect,
} from "react-table";

function MemberDataTable({ columns, data }) {
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    page,
    prepareRow,
    state,
    setGlobalFilter,
    selectedFlatRows, // Add this line to get the selected rows
  } = useTable(
    {
      columns,
      data,
      initialState: { pageIndex: 0, pageSize: 9 }, // Ï¥àÍ∏∞ ÌéòÏù¥ÏßÄ Ïù∏Îç±Ïä§ÏôÄ ÌéòÏù¥ÏßÄ ÌÅ¨Í∏∞ ÏÑ§Ï†ï
    },
    useFilters,
    useSortBy,
    usePagination,
    useRowSelect // Add useRowSelect hook
  );

  return (
    <div>
      {/* Í≤ÄÏÉâ ÏûÖÎ†•ÎûÄ */}
      <table
        {...getTableProps()}
        style={{
          border: "1px solid #E8E8E8",
          width: "100%",
          borderCollapse: "collapse",
        }}
      >
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th
                  {...column.getHeaderProps(column.getSortByToggleProps())}
                  style={{
                    padding: "8px",
                    cursor: "pointer",
                    border: "1px solid #E8E8E8",
                    borderBottom: "1px solid #E8E8E8", // Add a thicker bottom border for header cells
                    background: "#E1EDFF",
                  }}
                >
                  {column.render("Header")}
                  {column.isSorted ? (column.isSortedDesc ? " üîΩ" : " üîº") : ""}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {page.map((row) => {
            prepareRow(row);
            return (
              <tr
                {...row.getRowProps()}
                style={{ border: "1px solid #E8E8E8" }}
              >
                {" "}
                {/* Add border to rows */}
                {row.cells.map((cell) => (
                  <td
                    {...cell.getCellProps()}
                    style={{
                      padding: "8px",
                      border: "1px solid #E8E8E8",
                      borderBottom: "1px solid #E8E8E8", // Add a thicker bottom border for cells
                      background: "white",
                    }}
                  >
                    {cell.render("Cell")}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
      <div></div>
    </div>
  );
}

export default MemberDataTable;
