import axios from 'axios';
import React, { useState, useMemo } from 'react';
import { useEffect } from 'react';
import Notification from '../../components/Communication_Components/Notification';
import SearchRequire from '../../components/Communication_Components/SearchRequire';
import { styled } from 'styled-components';
import Pagination from 'react-js-pagination';
import StyledPaginationComponent from '../../components/Communication_Components/StylePaginationComponent';
import CommunityDataTable from '../../components/Communication_Components/CommunityDataTable';
import { useNavigate } from 'react-router-dom';

const Header2 = styled.h2`
  color: #000;
  font-family: 'NotoSansKR';
  font-size: 32px;
  font-style: normal;
  font-weight: 700;
  line-height: 44px; /* 137.5% */
  letter-spacing: -1.8px;
  margin-right: 717px;
`;

const DDiv = styled.div`
  background: #f6f6f6;
  margin: 0 auto;
`;

const Div = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background: #f6f6f6;
`;

const Container = styled.div`
  width: 890px;
`;

// Extract useToAnswerRouter outside the component
const useToAnswerRouter = () => {
  const navigate = useNavigate();
  return (id) => {
    navigate(`/communication/suggestion/${id}`);
  };
};

function SuggestionManager() {
  const [page, setPage] = useState(1);

  const handlePageChange = (page) => {
    setPage(page);
  };

  const fetchNotices = async () => {
    try {
      const response = await axios.get("http://54.180.86.22:8080/inquiries", {
        headers: { Authorization: localStorage.getItem('token') },
      });
      console.log(response.data);
    } catch (error) {
      console.log('Error fetching notices:', error);
    }
  };
  useEffect(() => {
    fetchNotices();
  }, []);

  const data = [
    {
      id: 1,
      bannerTitle: '배너 타이틀1',
      writer: "박지성",
      date: '2023-05-02',
      status: '게시',
      answerer: '총학',
      answerDate: '',
    },
    {
      id: 2,
      bannerTitle: '배너 타이틀2',
      writer: "김현승",
      date: '2023-04-15',
      status: '게시',
      answerer: '총학',
      answerDate: '2023-05-02',
    },
    // 다른 데이터도 동일한 방식으로 추가
  ];

  const checkboxColumn = {
    Header: '',
    accessor: 'checkbox',
    Cell: ({ row }) => <input type="checkbox" {...row.getToggleRowSelectedProps()} />,
  };

  const columns = useMemo(
    () => [
      checkboxColumn,
      { Header: 'No.', accessor: 'id' },
      { Header: '건의 제목', accessor: 'bannerTitle' },
      { Header: '작성자', accessor: 'writer' },
      { Header: '게시일', accessor: 'date' },
      { Header: '처리여부', accessor: 'status' },
      { Header: '답변자', accessor: 'answerer' },
      {
        Header: '답변일시',
        accessor: 'answerDate',
        Cell: ({ value, row }) => {
          const toAnswerRouter = useToAnswerRouter();
          if (value === '') {
            return <button onClick={() => toAnswerRouter(row.original.id)}>버튼</button>; // Render button when answerDate is empty
          } else {
            return value; // Render the answerDate value when not empty
          }
        },
      },
    ],
    []
  );

  return (
    <>
      <DDiv>
        <Div>
          <Header2>소통-건의관리</Header2>
          <Notification></Notification>
          <SearchRequire></SearchRequire>
          <Container>
            <CommunityDataTable columns={columns} data={data}></CommunityDataTable>
          </Container>
          <StyledPaginationComponent page={page} handlePageChange={handlePageChange} />
        </Div>
      </DDiv>
    </>
  );
}

export default SuggestionManager;
