import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

import styled from "styled-components";
import { BsChevronLeft } from "react-icons/bs";
import AWS, { Outposts } from "aws-sdk";
import axios from "axios";

AWS.config.update({
  accessKeyId: process.env.REACT_APP_S3_ACCESS_KEY,
  secretAccessKey: process.env.REACT_APP_S3_SECRET_ACCESS_KEY,
  region: process.env.REACT_APP_S3_REGION,
});

const ImgDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
  margin-top: 30px;
`;

const Result = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const PcImg = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center; /* Center content vertically */
  align-items: center; /* Center content horizontally */
  width: 700px;
  height: 205px;
  top: 393px;
  border-radius: 5px;
  border: 1px;
  /* gap: 30px; */
  margin-left: 50px;
  margin-right: 20px;
  overflow-x: hidden;
  background: white;
`;

const MobImg = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center; /* Center content vertically */
  align-items: center; /* Center content horizontally */
  width: 410px;
  height: 205px;
  border-radius: 5px;
  border: 1px;
  /* gap: 30px; */
  overflow-x: hidden;
  background: white;
  margin-right: 50px;
`;

const StyledLabel = styled.label`
  display: inline-block;
  width: 74px;
  height: 30px;
  border-radius: 5px;
  border: 1px solid #e8e8e8;
  cursor: pointer;
  background: white;
  text-align: center; /* Add text-align property to center the text horizontally */
  line-height: 30px; /* Add line-height property to center the text vertically */
  margin-top: 30px;
`;

const RemButton = styled.button`
  display: flex;
  width: 75px;
  height: 30px;
  border-radius: 5px;
  margin-top: 15px;
  padding: 5px;
  border: 1px solid #e8e8e8;
  cursor: pointer;
  background: white;
  text-align: center; /* Add text-align property to center the text horizontally */
  line-height: 30px; /* Add line-height property to center the text vertically */
  text-align: center;
  /* Admin/Body/B3-14 R */
  font-family: "Noto Sans KR";
  font-size: 14px;
  font-style: normal;
  font-weight: 400;
  line-height: 140%; /* 19.6px */
  letter-spacing: -0.56px;
`;

const Headers = styled.header`
  text-align: center;
  /* Admin/Body/B3-14 R */
  font-family: "Noto Sans KR";
  font-size: 14px;
  font-style: normal;
  font-weight: 400;
  line-height: 140%; /* 19.6px */
  letter-spacing: -0.56px;
  margin-bottom: 11px;
`;

const ImgP = styled.header`
  color: var(--admin-gray-disabled-button, #cfd4d9);
  text-align: center;
  /* Admin/Caption/C2-13 R */
  font-family: "Noto Sans KR";
  font-size: 13px;
  font-style: normal;
  font-weight: 400;
  line-height: 160%; /* 20.8px */
`;

const Div = styled.div`
  background: #f6f6f6;
  height: 100%;
`;

const Register = styled.div`
  margin-left: 50px;
  margin-bottom: 15px;
  padding-top: 26px;
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
`;

const Header = styled.header`
  color: #000;
  margin-left: 10px;
  /* Admin/Header/H1-32 B */
  font-family: "Noto Sans KR";
  font-size: 32px;
  font-style: normal;
  font-weight: 700;
  line-height: 140%; /* 44.8px */
`;

const RegisterDiv = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  width: Hug (1570px);
  height: 196px;
  top: 167px;
  border-radius: 4px;
  margin-left: 50px;
  /* overflow-x: hidden; */
  background: white;
  padding-top: 15px;
  padding-bottom: 15px;
  margin-right: 50px;
`;
const RowDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
  padding-left: 20px;
  margin-bottom: 15px;
`;

const Button = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  margin-top: 200px;
  padding-bottom: 30px;
`;
const Cancel = styled.button`
  width: 153px;
  height: 42px;
  padding: 10px;
  margin-right: 30px;
  border-radius: 5px;
  border: 1px;
  gap: 10px;
`;
const Submit = styled.button`
  width: 153px;
  height: 42px;
  padding: 10px;
  border-radius: 5px;
  border: 1px;
  gap: 10px;
`;

const Titlep = styled.header`
  width: 85px;
  height: 22px;
  margin-right: 31px;
  /* Admin/Body/B1-16 R */
  font-family: "Noto Sans KR";
  font-size: 16px;
  font-style: normal;
  font-weight: 400;
  line-height: 140%; /* 22.4px */
`;

const TextInput = styled.input`
  width: 450px;
  height: 35px;
  left: 120px;
  border-radius: 2px;
  border: 1px solid #e8e8e8;
  padding: 2px 10px;
  font-family: "Noto Sans KR";
  font-size: 15px;
  font-style: normal;
  font-weight: 400;
  line-height: 140%; /* 21px */
`;

const Pubp = styled.header`
  width: 74px;
  height: 22px;
  margin-right: 46px;
  font-family: "Noto Sans KR";
  font-size: 16px;
  font-style: normal;
  font-weight: 400;
  line-height: 140%; /* 22.4px */
`;

const Datep = styled.header`
  width: 59px;
  height: 22px;
  margin-right: 61px;
  font-family: "Noto Sans KR";
  font-size: 16px;
  font-style: normal;
  font-weight: 400;
  line-height: 140%; /* 22.4px */
`;

const Nump = styled.p`
  width: 74px;
  height: 22px;
  margin-right: 46px;
`;

const Select = styled.select`
  width: 90px;
  height: 35px;
  padding: 4px 12px 4px 12px;
  border-radius: 2px;
  border: 1px;
  border: 1px solid #e8e8e8;
  /* Admin/Body/B3-14 R */
  font-family: "Noto Sans KR";
  font-size: 14px;
  font-style: normal;
  font-weight: 400;
  line-height: 140%; /* 19.6px */
  letter-spacing: -0.56px;
`;

const Label = styled.header`
  margin-right: 20px;
  margin-left: 6px;
  font-family: "Noto Sans KR";
  font-size: 15px;
  font-style: normal;
  font-weight: 400;
  line-height: 140%; /* 21px */
`;

const DateInput = styled.input`
  width: 133px;
  height: 35px;
  border-radius: 2px;
  border: 1px solid var(--admin-gray-stroke, #e8e8e8);
  margin-right: 16px;
`;

const DateInput2 = styled.input`
  width: 133px;
  height: 35px;
  border-radius: 2px;
  border: 1px solid var(--admin-gray-stroke, #e8e8e8);
  margin-right: 16px;
`;

function BannerRegister() {
  const navigate = useNavigate();
  const [start, setstart] = useState();
  const [end, setend] = useState();

  const [filepc, setFilepc] = useState();
  const [filemob, setFilemob] = useState();
  const [isPublished, setIsPublished] = useState(true);
  //false-> 게시중 true -> 게시 종료
  const [outputOrder, setOutputOrder] = useState(null);
//출력순서 int
  const [imageUrl, setImageUrl] = useState("");
  const [imageViewpc, setImageViewpc] = useState();
  const [imageViewmob, setImageViewmob] = useState();

  const [title, setTitle] = useState(""); // State for the title input

  const s3 = new AWS.S3();

  const uploadToS3 = async () => {
    if (!filepc) {
      return;
    }
    const params = {
      Bucket: process.env.REACT_APP_S3_BUCKET,
      Key: "upload/" + `${Date.now()}.${filepc}`,
      Body: filepc,
    };
    const { Location } = await s3.upload(params).promise();
    setImageUrl(Location);
    console.log("uploading to s3", Location);
  };

  const handleFileSelectpc = (e) => {
    var f = e.target.value;
    let fileLength = f.length;
    let fileDot = f.lastIndexOf(".");
    let fileType = f.substring(fileDot + 1, fileLength);
    if (fileType == "jpeg" || fileType == "jpg" || fileType == "png") {
      setFilepc(e.target.files[0]);
      setImageViewpc(URL.createObjectURL(e.target.files[0]));
    } else {
      alert(".jpeg, .jpg, .png 파일만 업로드 가능합니다");
      setFilepc(null);
    }
  };
  const handleFileSelectmob = (e) => {
    var f = e.target.value;
    let fileLength = f.length;
    let fileDot = f.lastIndexOf(".");
    let fileType = f.substring(fileDot + 1, fileLength);
    if (fileType == "jpeg" || fileType == "jpg" || fileType == "png") {
      setFilemob(e.target.files[0]);
      setImageViewmob(URL.createObjectURL(e.target.files[0]));
    } else {
      alert(".jpeg, .jpg, .png 파일만 업로드 가능합니다");
      setFilemob(null);
    }
  };

  const handleRemoveImage = () => {
    setImageViewpc(null);
  };

  const handleRemoveImage1 = () => {
    setImageViewmob(null);
  };

  const handleCancelClick = () => {
    const shouldCancel = window.confirm(
      "배너 등록을 취소하시겠습니까?\n취소시 작성 중인 내용이 사라집니다."
    );
    if (shouldCancel) {
      // Perform any additional actions on cancel (if needed)
      console.log("Cancel clicked!");
      navigate("/main/banner");
    }
  };
  const handleRegisterClick = async () => {
    const shouldRegister = window.confirm(
      "배너를 등록하시겠습니까?\n해당 정보로 배너가 등록됩니다."
    );
    if (shouldRegister) {
      try {
        // Upload the file to S3 and get the image URL
        await uploadToS3();
        if (!imageUrl) {
          alert(
            "이미지 업로드 중입니다. 다시 한 번 등록하기 \n버튼을 눌러주세요."
          );
          return;
        }

        const formattedStart = start + "T00:00:00";
        const formattedEnd = end + "T00:00:00";

        const data = {
          title: title,
          image: imageUrl,
          hide:isPublished,
         // start: formattedStart, // 변환된 날짜 사용
         // end: formattedEnd, // 변환된 날짜 사용
        };
        console.log(data); // 데이터 객체를 출력합니다.
        const response = await axios.post(
          "http://54.180.86.22:8080/api/banners",
          data,
          {
            headers: { Authorization: localStorage.getItem("token") },
          }
        );

        console.log("API Responsess:", response.data);
        navigate("/main/banner");
      } catch (error) {
        alert("로그인이 필요합니다.");
        console.error("Error in handleRegisterClick:", error);
        throw error;
      }
    }
  };

  const handleButtonClick = () => {
    navigate("/main/banner"); // Make sure the path matches the one defined in App.js
  };
  /*
  const isAllDataFilled = () => {
    return start&&end&& title && isPublished && outputOrder && imageViewpc !== null
    && imageViewmob !== null


  };*/

  const isAllDataFilled = () => {
    return (
      start &&
      end &&
      title &&
      isPublished &&
      outputOrder !== null &&
      imageViewpc !== null &&
      imageViewmob !== null 

    )
  };
  
  

  const handleChange = (event) => {
    setIsPublished(event.target.value === 'true');
  };

  console.log("1212",outputOrder)




  return (
    <Div>
      <Register>
        <BsChevronLeft
          onClick={handleButtonClick}
          size={25}
          color="#00000080"
          stroke-width="1"
          stroke="#00000080"
        />
        <Header>배너 등록하기</Header>
      </Register>
      <RegisterDiv>
        <RowDiv>
          <Titlep>배너 타이틀</Titlep>
          <TextInput
            type="text"
            placeholder="제목입력"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </RowDiv>
        <RowDiv>
          <Pubp>게시 여부</Pubp>
          <input 
  type="radio" 
  id="pub" 
  value="true"
  name="publishStatus" 
  checked={isPublished}
  onChange={ handleChange} 
/>


          <Label htmlFor="pub">게시</Label>
          <input 
  type="radio" 
  id="unpub" 
  value="false"
  name="publishStatus" 
  checked={!isPublished}
  onChange={handleChange} 
/>
          <Label htmlFor="unpub">미게시</Label>
        </RowDiv>
        <RowDiv>
          <Datep>게시기간</Datep>
          <DateInput
            type="date"
            id="Sdate"
            //defaultValue="yyyy-mm-dd"
            value={start}
            onChange={(e) => setstart(e.target.value)}
          />
          <DateInput2
            type="date"
            id="Ddate"
            //defaultValue="yyyy-mm-dd"
            value={end}
            onChange={(e) => setend(e.target.value)}
          />
        </RowDiv>
        <RowDiv>
        <Datep>출력순서</Datep>
<Select 
  name="num" 
  value={outputOrder === null ? "" : outputOrder} 
  onChange={(e) => setOutputOrder(e.target.value ? parseInt(e.target.value) : null)}
>
    <option value="">-</option>
    <option value="1">1</option>
    <option value="2">2</option>
    <option value="3">3</option>
    <option value="4">4</option>
</Select>

        </RowDiv>
      </RegisterDiv>
      <ImgDiv>
        {imageViewpc ? (
          <PcImg>
            <img
              src={imageViewpc}
              alt="Uploaded"
              style={{ width: "520px", height: "130px" }}
            />
            <RemButton onClick={handleRemoveImage}>이미지 제거</RemButton>
          </PcImg>
        ) : (
          <PcImg>
            <Headers>배너 이미지 삽입(PC뷰)</Headers>
            <ImgP>드래그 업로드 또는 내 PC 검색</ImgP>
            <ImgP>이미지 사이즈(1440*360 px 권장)</ImgP>
            <StyledLabel htmlFor="fileInput1">내 PC</StyledLabel>
            <input
              type="file"
              id="fileInput1"
              onChange={handleFileSelectpc}
              accept="image/*"
              style={{ display: "none" }}
            />
          </PcImg>
        )}
        {imageViewmob ? (
          <MobImg>
            <img
              src={imageViewmob}
              alt="Uploaded"
              style={{ width: "203px", height: "130px" }}
            />
            <RemButton onClick={handleRemoveImage1}>이미지 제거</RemButton>
          </MobImg>
        ) : (
          <MobImg>
            <Headers>배너 이미지 삽입(모바일뷰)</Headers>
            <ImgP>드래그 업로드 또는 내 PC 검색</ImgP>
            <ImgP>이미지 사이즈(375*240 px 권장)</ImgP>
            <StyledLabel htmlFor="fileInput2">내 PC</StyledLabel>
            <input
              type="file"
              id="fileInput2"
              onChange={handleFileSelectmob}
              mob
              accept="image/*"
              style={{ display: "none" }}
            />
          </MobImg>
        )}
      </ImgDiv>
      <Button>
        <Cancel onClick={handleCancelClick}>취소</Cancel>
        <Submit 
                            style={
                              isAllDataFilled()
                                ? { backgroundColor: "#0000FF", color: "#FFFFFF" }
                                : { backgroundColor: "#D3D3D3", color: "#808080" }
                            }
        onClick={handleRegisterClick}>등록하기</Submit>
      </Button>
    </Div>
  );
}

export default BannerRegister;
