import React from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import checkbox from '../../Assets/Img/Check Box.png'
import AWS from "aws-sdk";
import axios from "axios"; 
import {BsChevronLeft} from "react-icons/bs";

AWS.config.update({
  accessKeyId: process.env.REACT_APP_S3_ACCESS_KEY,
  secretAccessKey: process.env.REACT_APP_S3_SECRET_ACCESS_KEY,
  region: process.env.REACT_APP_S3_REGION,
});

const DateInput2 = styled.input`
width: 133px;
height: 35px;
border-radius: 2px;
border: 1px solid var(--admin-gray-stroke, #E8E8E8);
margin-right: 16px;


`;
const Register = styled.div`
  margin-left: 60px;
  margin-bottom: 15px;
  padding-top: 26px;
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
`;
const Div = styled.div`
  background: #F6F6F6;
  margin: 0 auto;
  height: 100%;
`;
const DDiv= styled.div`
      display: flex;
  flex-direction: column;
  align-items: center;
`
const Body3 = styled.div`
  font-size: ${(props) => props.theme.Web_fontSizes.Body3};
  font-weight: ${(props) => props.theme.fontWeights.Body3};
  line-height: ${(props) => props.theme.LineHeight.Body3};
  letter-spacing: -0.56px;
  font-style: normal;
  font-family: "NotoSansKR";
  color: var(--black, #000);
  text-align: center;
  margin-top: 30px;
`;
const Pubp = styled.header`
width: 74px;
height: 22px;
margin-right: 48px;
font-family: 'Noto Sans KR';
font-size: 16px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 22.4px */

`;
const Select = styled.select`
width: 90px;
height: 35px;
padding: 4px 12px 4px 12px;
border-radius: 2px;
border: 1px;
border: 1px solid #E8E8E8;
/* Admin/Body/B3-14 R */
font-family: 'Noto Sans KR';
font-size: 14px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 19.6px */
letter-spacing: -0.56px;
`;
const DateInput = styled.input`
width: 133px;
height: 35px;
border-radius: 2px;
border: 1px solid var(--admin-gray-stroke, #E8E8E8);
margin-right: 16px;
color: var(--admin-gray-disabled-button, #CFD4D9);

/* Admin/Body/B1-16 R */
font-family: Noto Sans KR;
font-size: 16px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 22.4px */

`;

const Label = styled.header`
//background: blue;
margin-right: 20px;
margin-left: 6px;
font-family: 'Noto Sans KR';
font-size: 15px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 21px */
`;
const Datep = styled.header`
width: 59px;
height: 22px;
margin-right: 61px;
font-family: 'Noto Sans KR';
font-size: 16px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 22.4px */
`;
const Titlep = styled.header`
width: 89px;
height: 22px;
margin-right: 31px;
/* Admin/Body/B1-16 R */
font-family: 'Noto Sans KR';
font-size: 16px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 22.4px */
`;
const RedAsterisk = styled.span`
  font-size: ${(props) => props.theme.Web_fontSizes.Body3};
  font-weight: ${(props) => props.theme.fontWeights.Body3};
  line-height: ${(props) => props.theme.LineHeight.Body3};
  letter-spacing: -0.56px;
  font-style: normal;
  font-family: "NotoSansKR";
  color: var(--black, #000);
  text-align: center;
  color: red;
`;

const Caption2 = styled.div`
  font-size: ${(props) => props.theme.Web_fontSizes.Caption2};
  font-weight: ${(props) => props.theme.fontWeights.Caption2};
  line-height: ${(props) => props.theme.LineHeight.Caption2};
  font-style: normal;
  font-family: "NotoSansKR";
  color: var(--admin-gray-disabled-button, #cfd4d9);
  text-align: center;
  margin-bottom: 30px;
`;

const RegisterDiv = styled.div`
  display: flex;
  flex-direction: column;
 
  width: 890px;
height: 350px;
  margin-top: 10px;
  padding: 20px;
  gap: 15px;
   //background-color: red; 
   border-radius: 4px;
background: var(--white, #FFF);
`;

const PopupContentsDiv = styled.div`
  display: flex;
  flex-direction: row;

width: 890px;
height: 240px;
border-radius: 4px;
//background: var(--white, #FFF);
   background-color: green; 
   margin-top: 30px;
`;
const RowDiv3 = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
  padding-left: 20px;
  margin-bottom: 15px;
`;
const Checked= styled.div`
width:74px;
height:22px;

`
const DDDIV= styled.div`
display: flex;
flex-direction: row;
margin-left: 47px;
`
const Selectgigi = styled.div`
width:65px;
height:21px;
color: #000;
margin-top: 2px;
margin-left: 6px;

/* Admin/Body/B2-15 R */
font-family: Noto Sans KR;
font-size: 15px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 21px */
`

