const SelectImg = styled.img`
  width: 100%;
  height: 100%;
`;
const Header = styled.header`
color: #000;
margin-left: 10px;
/* Admin/Header/H1-32 B */
font-family: 'Noto Sans KR';
font-size: 32px;
font-style: normal;
font-weight: 700;
line-height: 140%; /* 44.8px */
`;



const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;   // Ensures the image covers the div completely
  object-position: center;  // Centers the image within the div


  display: ${props => (props.show ? 'block' : 'none')};
`;






function Popupregister() {
  const navigate = useNavigate();

  const [file, setFile] = useState();

  const [imageUrl, setImageUrl] = useState();
//imageurl


  const [imageLink, setImageLink] = useState("");
  // 입력되는 imageLink


  const [imageView, setImageView] = useState();

  const [title, setTitle] = useState(""); 
  // 제목 state

  const [start,setstart] =useState();
  const [end,setend] =useState();


  const [isDesktop, setIsDesktop] = useState(true);
  // desktop true
  // 모바일 false

  const [isAllVisitors, setIsAllVisitors] = useState(true);
// 적용조건 모든방문자 true, 로그인 방문자false

  const [selectedOption, setSelectedOption] = useState(0);
 
  // 0 닫기 , 1 오늘 하루 다시 보지 않기, 2 표시 안함
  const [isOpen, setIsOpen] = useState(false);
  //새창에서 여는 것 true,false로 저장함.


  const [topValue, setTopValue] = useState(null);
  // 위로 부터 값 int로 저장

  const [leftValue, setLeftValue] = useState(null);
  // 왼쪽으로 부터 값 int로 저장


  const [isTopFocused, setIsTopFocused] = useState(false);
  //마우스 커서 -> 자동으로 px붙이기 위한
  const [isLeftFocused, setIsLeftFocused] = useState(false);

    // Handle change in the top input
  
    const [postok,setpostok]= useState(0);

    // 1게시 2 대기 0 미게시
    const s3 = new AWS.S3();



    const handleTopChange = (e) => {
      const value = e.target.value;
      if (!isNaN(value) && value !== '') {
        setTopValue(parseFloat(value));
      } else {
        setTopValue(null);
      }
    };
      // Handle change in the left input
  const handleLeftChange = (e) => {
    const value = e.target.value;
    if (!isNaN(value) && value !== '') {
      setLeftValue(parseFloat(value));
    } else {
      setLeftValue(null);
    }
  };


  const handleOpen = () => {
    setIsOpen(prevState => !prevState);
  };


  const handleInputChangeop = (event) => {
      setSelectedOption(Number(event.target.value));
  };
  const handleRadioChange = (event) => {
    setIsAllVisitors(event.target.value === 'true');
  };
  const handleCheck = (device) => {
    setIsDesktop(device === 'Desktop');
  };


  

  const handleInputChange = (event) => {
    // Convert the string value from the radio button to a number
    const numericalValue = Number(event.target.value);
    setpostok(numericalValue);
};


  const handleButtonClick = () => {
    navigate('/main/banner'); // Make sure the path matches the one defined in App.js
  };

  const handleCancelClick = () => {
    const shouldCancel = window.confirm(
      "배너 등록을 취소하시겠습니까?\n취소시 작성 중인 내용이 사라집니다."
    );
    if (shouldCancel) {
      console.log("Cancel clicked!");
      navigate("/dataCollection/studentCouncil");
    }
  };

  const convertToDatabaseFormat = (date) => {
    const parsedDate = new Date(date);
    const year = parsedDate.getFullYear();
    const month = String(parsedDate.getMonth() + 1).padStart(2, "0");
    const day = String(parsedDate.getDate()).padStart(2, "0");
  
    const hours = String(parsedDate.getHours()).padStart(2, "0");
    const minutes = String(parsedDate.getMinutes()).padStart(2, "0");
    const seconds = String(parsedDate.getSeconds()).padStart(2, "0");
  
    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
  };
  


console.log(convertToDatabaseFormat(start),"Aaa");
  const handleRegisterClick = async () => {
    const shouldRegister = window.confirm(
      "배너를 등록하시겠습니까?\n해당 정보로 배너가 등록됩니다."
    );
  
    if (shouldRegister) {
      try {
        // Upload the file to S3 and get the image URL
        await uploadToS3();
  
        if (!imageUrl) {
          alert("이미지 업로드 중입니다. 다시 한 번 등록하기 \n버튼을 눌러주세요.");
          return;
        }
        
        // Proceed with registration
        const data = {
          title : title,
          //name: localStorage.getItem("userName"),
          image: imageUrl,
          locationVertical: topValue,
          locationHorizontal: leftValue,
          targetDevice:isDesktop,
          targetVisitor:isAllVisitors,
          status:postok,
          link:imageLink,
          start: convertToDatabaseFormat(start) ,
          end:convertToDatabaseFormat(end) 

         // end:end
        };
  
        const response = await axios.post(
          "http://54.180.86.22:8080/api/popUps",
          data,
          {
            headers: { Authorization: localStorage.getItem("token") },
          }
        );
  
        console.log("API Response:", response.data);
        navigate("/main/popup");
      } catch (error) {
        alert("로그인이 필요합니다.");
        console.error("Error in handleRegisterClick:", error);
        throw error;
      }
    }
  };
  

  const handleFileSelect = (e) => {
    var f = e.target.value;
    let fileLength = f.length;
    let fileDot = f.lastIndexOf(".");
    let fileType = f.substring(fileDot + 1, fileLength);
    if (fileType == "jpeg" || fileType == "jpg" || fileType == "png") {
      setFile(e.target.files[0]);
      setImageView(URL.createObjectURL(e.target.files[0]));
    } else {
      alert(".jpeg, .jpg, .png 파일만 업로드 가능합니다");
      setFile(null);
    }
  };
  const handledeltedButtonClick = ()=>{
    setImageView(null);
  }

  const uploadToS3 = async () => {
    if (!file) {
      return;
    }
    const params = {
      Bucket: process.env.REACT_APP_S3_BUCKET,
      Key: "upload/" + `${Date.now()}.${file.name}`,
      Body: file,
    };
    const { Location } = await s3.upload(params).promise();
    setImageUrl(Location);
    console.log("uploading to s3", Location);
  };
  const isAllDataFilled = () => {
    return start&&end&& title && imageView && imageLink && topValue !== null && leftValue !== null;
  };
  
  return (
    <Div>
      
            <Register>
        <BsChevronLeft onClick={handleButtonClick} size={25} color="#00000080" stroke-width="1" stroke="#00000080"/>
        <Header>팝업 등록하기</Header>
        </Register>
        <DDiv>
      <RegisterDiv>
      <RowDiv>
      <Titlep>팝업 타이틀</Titlep>
      <TextInput type="text" placeholder="제목입력"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
      />
      </RowDiv>
      <RowDiv1>
      <Pubp>게시 여부</Pubp>
      <input type="radio" id="post"  name="pubStatus" value="1" checked={postok === 1} onChange={handleInputChange} />
               
      <Label htmlFor="post" >게시</Label>
      <input type="radio" id="standby" name="pubStatus" value="2" checked={postok === 2} onChange={handleInputChange} />
               
      <Label htmlFor="standby">대기</Label>
      <input type="radio" id="non-post" name="pubStatus" value="0" checked={postok=== 0} onChange={handleInputChange} />
             
      <Label htmlFor="non-post">미게시</Label>
      </RowDiv1>
      <RowDiv>
      <Datep>게시기간</Datep>
      <DateInput type="date" id="Sdate" 
      defaultValue="yyyy-mm-dd" 
      value={start}
      onChange={(e) => setstart(e.target.value)}
      />
      <DateInput2 type="date" id="Ddate" 
      defaultValue="yyyy-mm-dd" 
      value={end}
      
      onChange={(e) => setend(e.target.value)}
      />
      </RowDiv>
      <RowDiv3>
        <Checked>
          대상 기기
        </Checked>
        <DDDIV>
        <Checkbox onClick={() => handleCheck('Desktop')}>
        <Image src={checkbox} show={isDesktop} />
      </Checkbox>
      <Selectgigi>
        데스크톱
      </Selectgigi>
      <Checkbox onClick={() => handleCheck('mobile')}>
        <Image src={checkbox} show={!isDesktop} />
      </Checkbox>
      <Selectgigi>
        모바일
      </Selectgigi>

</DDDIV>

      
        
      
      </RowDiv3>

      <RowDiv1>
      <Pubp>적용조건</Pubp>
   
      <input 
        type="radio" 
        id="allVisitors" 
        name="visitorStatus" 
        value="true"
        checked={isAllVisitors} 
        onChange={handleRadioChange} 
      />
      <Label htmlFor="allVisitors">모든 방문자</Label>

      <input 
        type="radio" 
        id="loginVisitors" 
        name="visitorStatus" 
        value="false"
        checked={!isAllVisitors} 
        onChange={handleRadioChange} 
      />
      <Label htmlFor="loginVisitors">로그인 방문자</Label>
      </RowDiv1>
      <RowDiv1>
      <Pubp>우측 상단 닫기 조건</Pubp>
      <input 
                type="radio" 
                id="close" 
                name="selection" 
                value="0"
                checked={selectedOption === 0} 
                onChange={handleInputChangeop} 
            />
            <Label htmlFor="close">닫기</Label>

            <input 
                type="radio" 
                id="dontShowToday" 
                name="selection" 
                value="1"
                checked={selectedOption === 1} 
                onChange={handleInputChangeop} 
            />
            <Label htmlFor="dontShowToday">오늘 하루 다시 보지 않기</Label>

            <input 
                type="radio" 
                id="unpub" 
                name="selection" 
                value="2"
                checked={selectedOption === 2} 
                onChange={handleInputChangeop} 
            />
            <Label htmlFor="unpub">표시 안함</Label>
      </RowDiv1>
      </RegisterDiv>
      <PopupContentsDiv>

    
          <DropzoneDiv>



            {imageView ? (
              <>
              <FileImage src={imageView} alt="uploaded" />
              <Removeimg onClick={handledeltedButtonClick}>
              이미지 제거
                          </Removeimg>
                          </>
            ) : (
              <>
                <Body3>
                  팝업 이미지 삽입 <RedAsterisk>*</RedAsterisk>
                </Body3>
                <Caption2>
                  드래그 업로드 또는 내 PC 검색
                  <br />
                  이미지 사이즈(250*250 권장)
                </Caption2>
                <FileInputLabel>
                  내 PC
                  <FileInput
                    type="file"
                    id="fileInput"
                    onChange={handleFileSelect}
                  />
                </FileInputLabel>
              </>
            )}  

             
         
          </DropzoneDiv>
          <FILELINK>
            <Popuplace>
              <LEtter>
              팝업 위치 *
              </LEtter>
              <BOX>
                <Upword >
                위로부터
                </Upword>
                <Upinput
 
       value={isTopFocused ? topValue : (topValue !== null ? `${topValue}px` : '')}
        onChange={handleTopChange}
      onFocus={() => setIsTopFocused(true)}
       onBlur={() => setIsTopFocused(false)}
      />
                <Leftword>왼쪽으로부터</Leftword>
                <Leftinput     type='text'
        value={isLeftFocused ? leftValue : (leftValue !== null ? `${leftValue}px` : '')}
        onChange={handleLeftChange}
        onFocus={() => setIsLeftFocused(true)}
        onBlur={() => setIsLeftFocused(false)}
        />

              </BOX>

            </Popuplace>
            <ImgLink>
  <LEtter2>이미지 링크</LEtter2>
  <BOXLink       type="text"
      placeholder="이미지 링크를 입력하세요."
      value={imageLink}
      onChange={(e) => setImageLink(e.target.value)}/>

</ImgLink>

            <Opennew>
            <Checkbox onClick={handleOpen}>
      <Image src={checkbox} show={isOpen} />
    </Checkbox>
              <Openword>
              새 창에서 열기
              </Openword>
             
            </Opennew>


          </FILELINK>
 
      </PopupContentsDiv>
      {/* ... */}

      <Button>
        <Cancel onClick={handleCancelClick}>취소</Cancel>
        <Submit onClick={handleRegisterClick}
          style={
            isAllDataFilled()
              ? { backgroundColor: "#0000FF", color: "#FFFFFF" }
              : { backgroundColor: "#D3D3D3", color: "#808080" }
          }
        >등록하기</Submit>
      </Button>
</DDiv>
    </Div>
  );
}

export default Popupregister;
