import React, { useState, useMemo, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import DataTableposting from "../../components/Banner_Components/DataTable";
import DataTablepostend from "../../components/Banner_Components/DataTableend";
import PubBanner from "../../components/Banner_Components/PubBanner";

import StyledPaginationComponentposting from "../../components/Banner_Components/StylePaginationComponentposting";
import StyledPaginationComponentpostend from "../../components/Banner_Components/StylePaginationComponentpostend";
// import { GlobalStyle, theme } from "../../Style/theme";
import axios from "axios";
import ReactModal from "react-modal";
import { ImportContacts } from "@mui/icons-material";
ReactModal.setAppElement("#root"); // 모달의 부모로 루트 엘리먼트 설정

const DDiv = styled.div`
  background: #f6f6f6;
  margin: 0 auto;
`;
const Div = styled.div`
  margin-right: 50px;
`;
const Manage = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  margin-left: 50px;
  margin-bottom: 15px;
  padding-top: 26px;
`;
const RegisterButton = styled.button`
  width: 153px;
  height: 42px;
  padding: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 10px;
  border-radius: 5px;
  background: var(--admin-blue-default-blue, #0079e0);
  color: white;
  border: #0079e0;
  font-family: "Noto Sans KR";
  font-size: 16px;
  font-style: normal;
  font-weight: 500;
  line-height: 140%;
`;

const RegisterDiv = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  //align-items: flex-start;
  width: Hug(1570px);
