const handledeletedClick = async () => {
  try {
    setDeletionInProgress(true);
    const deletePromises = SelectedRowspostend.map(async (row) => {
      // Assuming each row contains an 'id' property which you want to delete
      await axios.delete(
        `http://54.180.86.22:8080/api/banners/${row.id}`,
        {
          headers: { Authorization: localStorage.getItem("token") },
        }
      );
    });

    await Promise.all(deletePromises);

    // Refresh the data or remove deleted items from state
    const updatedBannerData = bannerData.filter(
      (item) => !SelectedRowspostend.includes(item)
    );
    setBannerData(updatedBannerData);
    setSelectedRowspostend([]); // Clear the selection
    setRemoveBannerModal(false); // Close the modal
    navigate("/main/banner");
    setDeletionInProgress(false);
  } catch (error) {
    alert("There was an error deleting the items. Please try again.");
    console.error("Error in handledeletedClick:", error);
    setDeletionInProgress(false); 
  }
};

  async function updateBannerHideValue(bannerId, hideValue) {
    try {
      const bannerToUpdate = bannerData.find(banner => banner.id === bannerId);
      if (bannerToUpdate) {
        const updatedBannerData = {
          ...bannerToUpdate,
          hide: hideValue
        };
        await axios.patch(`http://54.180.86.22:8080/api/banners/${bannerId}`, updatedBannerData, {
          headers: { Authorization: localStorage.getItem("token") },
        });
      }
    } catch (error) {
      console.error(`Error updating hide value for banner with ID ${bannerId}:`, error);
    }
  }
  
  function toggleHideValues() {
    const updatedBannerData = bannerData.map((banner) => {
      if (SelectedRowsposting.some((selected) => selected.id === banner.id)) {
        // Update the hide value in the server
        updateBannerHideValue(banner.id, !banner.hide);
  
        return {
          ...banner,
          hide: !banner.hide,
        };
      }
      return banner;
    });
  
    setBannerData(updatedBannerData);
    setSelectedRowsposting([]); // Clear the selection
  }
  
  


