import axios from "axios";
import React, { useState } from "react";
import { useEffect } from "react";
import PinImg from "../../Assets/Img/Pin.svg";
import SearchRequire from "../../components/DataCollection_Components/SearchRequire";
import { styled } from "styled-components";
import { useNavigate } from "react-router-dom";
import StyledPaginationComponent from "../../components/DataCollection_Components/StylePaginationComponent";
import DataTable from "../../components/DataCollection_Components/DataCollectionTable";

function StudentCouncil() {
  const navigate = useNavigate();
  const [page, setPage] = useState(1);
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [displayedData, setDisplayedData] = useState([]);
  const itemsPerPage = 10;

  const readDataNotice = async () => {
    try {
      const response = await axios.get(
        "http://54.180.86.22:8080/api/shareNotices",
        {
          headers: { Authorization: localStorage.getItem("token") },
        }
      );
      setData(response.data);
      setFilteredData(response.data);
    } catch (error) {
      console.log("Error fetching notices:", error);
    }
  };


  const isToday = (date) => {
    const today = new Date();
    return (
      date.getDate() === today.getDate() &&
      date.getMonth() === today.getMonth() &&
      date.getFullYear() === today.getFullYear()
    );
  };


  const isWithinXDays = (date, days) => {
    const today = new Date();
    const targetDate = new Date(date);
    const diffTime = Math.abs(today - targetDate);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays <= days;
  };

  const isWithinXMonths = (date, months) => {
    const today = new Date();
    const targetDate = new Date(date);
    const diffMonths =
      (today.getFullYear() - targetDate.getFullYear()) * 12 +
      today.getMonth() -
      targetDate.getMonth();
    return diffMonths <= months;
  };

  const isWithinXYears = (date, years) => {
    const today = new Date();
    const targetDate = new Date(date);
    const diffYears = today.getFullYear() - targetDate.getFullYear();
    return diffYears <= years;
  };

  const isWithinDateRange = (date, startDate, endDate) => {
    return date >= startDate && date <= endDate;
  };

  const handleSearchFilters = (filters) => {
    const {
      selectedOption,
      dateButtonClicked,
      startDate,
      endDate,
      searchInput,
    } = filters;

    let filteredResults = data;
   
    const searchInputLower = searchInput.toLowerCase();

    if (selectedOption === "제목만") {
      filteredResults = filteredResults.filter((item) =>
        item.title.toLowerCase().includes(searchInputLower)
      );
    } else if (selectedOption === "제목, 내용") {
      filteredResults = filteredResults.filter(
        (item) =>
          item.title.toLowerCase().includes(searchInputLower) ||
          item.content.toLowerCase().includes(searchInputLower)
      );
    } else if (selectedOption === "내용") {
      filteredResults = filteredResults.filter((item) =>
        item.content.toLowerCase().includes(searchInputLower)
      );
    } else if (selectedOption === "작성자") {
      filteredResults = filteredResults.filter((item) => {
        if (searchInput.substring("총학생회")) {
          return item.category === 1;
        } else if (searchInput.substring("총동아리연합회")) {
          return item.category === 2;
        } else if (searchInput.substring("중앙선거관리위원회")) {
          return item.category === 3;
        } else if (searchInput === "") {
          return true;
        } else {
          return false;
        }
      });
    }

    if (dateButtonClicked[0]) {

      filteredResults = filteredResults.filter((item) =>
        isToday(new Date(item.createdAt))
      );
    } else if (dateButtonClicked[1]) {

      filteredResults = filteredResults.filter((item) =>
        isWithinXDays(new Date(item.createdAt), 7)
      );
    } else if (dateButtonClicked[2]) {

      filteredResults = filteredResults.filter((item) =>
        isWithinXMonths(new Date(item.createdAt), 1)
      );
    } else if (dateButtonClicked[3]) {

      filteredResults = filteredResults.filter((item) =>
        isWithinXMonths(new Date(item.createdAt), 3)
      );
    } else if (dateButtonClicked[4]) {
 
      filteredResults = filteredResults.filter((item) =>
        isWithinXMonths(new Date(item.createdAt), 6)
      );
    } else if (dateButtonClicked[5]) {

      filteredResults = filteredResults.filter((item) =>
        isWithinXYears(new Date(item.createdAt), 1)
      );
    } else if (startDate && endDate) {

      filteredResults = filteredResults.filter((item) =>
        isWithinDateRange(
          new Date(item.createdAt),
          new Date(startDate),
          new Date(endDate)
        )
      );
    }

    if (
      selectedOption === "" &&
      dateButtonClicked.every((btn) => !btn) &&
      !startDate &&
      !endDate
    ) {

      filteredResults = data;
    }

    setFilteredData(filteredResults);
  };

  const handlePageChange = (page) => {
    setPage(page);
    const startIndex = (page - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
  
    const sortedData = [...filteredData];
    sortedData.sort((a, b) => (b.pin ? 1 : a.pin ? -1 : 0)); 
  
    const pageData = sortedData.slice(startIndex, endIndex);
    setDisplayedData(pageData);
  };
  const handleButtonClick = () => {
    navigate("/dataRegister");
  };

  //"[오늘 등록된 새 글 n건] 검색 결과 n건 검색 되었습니다."" 를 위한 함수
  const calculateNewPostsCount = () => {
    const currentDate = new Date();
    //한국 시간으로 변경
    const koreanTime = new Date(currentDate.getTime() + 9 * 60 * 60 * 1000);
    const currentDateString = koreanTime.toISOString().slice(0, 10);
    // console.log(currentDateString);
    const newPosts = filteredData.filter((item) => {
      const postDate = new Date(item.createdAt);
      const postDateString = postDate.toISOString().slice(0, 10);
      // console.log("postDate:", postDateString);
      return postDateString === currentDateString;
    });
    return newPosts.length;
  };

  const newPostsCount = calculateNewPostsCount();

  const checkboxColumn = {
    Header: "",
    accessor: "checkbox",
    Cell: ({ row }) => (
      <input type="checkbox" {...row.getToggleRowSelectedProps()} />
    ),
  };

  const getCategoryName = (categoryId) => {
    switch (categoryId) {
      case 1:
        return "총학생회";
      case 2:
        return "총동아리연합회";
      case 3:
        return "중앙선거관리위원회";
      default:
        return " ";
    }
  };

  const downloadFile = async (filePath, fileName) => {
    try {
      const response = await axios.get(filePath, {
        responseType: "blob", 
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));

      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", fileName);
      document.body.appendChild(link);

      link.click();

      link.parentNode.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error("Error downloading file:", error);
    }
  };

  const columns = React.useMemo(
    () => [
      checkboxColumn,
      { Header: "No.", accessor: "id" },
      {
        Header: "제목",
        accessor: "title",
        Cell: ({ row }) => (
          <TitleCell>
            {row.original.pin && <img src={PinImg} alt="Pin" />}
            {row.original.title}
          </TitleCell>
        ),
      },

      { Header: "작성자", accessor: "category" },
      {
        Header: "파일",
        accessor: "files",
        Cell: ({ value }) => (
          <FileLink>
            {value.map((file, index) => (
              <span
                key={index}
                onClick={() => downloadFile(file.path, file.title)} // Add this onClick handler
              >
                {file.title}
                {index < value.length - 1 ? ", " : ""}
              </span>
            ))}
          </FileLink>
        ),
      },
      { Header: "게시일", accessor: "createdAt" },
      { Header: "조회수", accessor: "viewCnt" },
      {
        Header: "수정",
        accessor: "edit",
        Cell: () => <EditBtn>수정</EditBtn>,
      },
    ],
    []
  );
  useEffect(() => {
    readDataNotice();
  }, []);
  useEffect(() => {
    setFilteredData(data);
    setDisplayedData(filteredData.slice(0, itemsPerPage));
  }, [data]);

  useEffect(() => {
    
    const startIndex = (page - 1) * itemsPerPage;

    const endIndex = startIndex + itemsPerPage;
    
    const pageData = filteredData.slice(startIndex, endIndex);
 
    setDisplayedData(pageData);
  }, [filteredData, page]);

  return (
    <Div>
      <RowDiv>
        <Header2>자료집 - 총학생회</Header2>
        <RegisterBtn onClick={handleButtonClick}>자료 등록하기</RegisterBtn>
      </RowDiv>
      <SearchRequire onSearchFilters={handleSearchFilters} />
      <AlertDiv>
        <p>
          [오늘 등록된 새 글 <P>{newPostsCount}</P>건] 검색 결과{" "}
          <P>{filteredData.length}</P>건 검색 되었습니다.
        </p>
        <DeleteBtn>삭제</DeleteBtn>
      </AlertDiv>
      <DataDiv>
        {filteredData.length > 0 && ( 
          <DataTable
            columns={columns}
            filteredData={displayedData.map((item) => ({
              ...item,
              category: getCategoryName(item.category),
            }))}
          />
        )}
      </DataDiv>
      <StyledPaginationComponent
        page={page}
        handlePageChange={handlePageChange}
        totalItemsCount={filteredData.length}
        itemsPerPage={itemsPerPage}
      />
    </Div>
  );
}

