import React, { useState } from "react";
import styled from 'styled-components';
import notification from "../../Assets/Img/notification.png";
import pencil from "../../Assets/Img/pencil.png";
const Div = styled.div`
width: 890px;
height: 83px;
display: flex;
margin-bottom: 20px;
background-color:  white;
`;
const Img = styled.img`
width: 40px ;
height : 40px ;
margin : auto 0;
`;

const Input = styled.input`
  font-size: ${props => props.theme.Web_fontSizes.Header4};
  font-weight: ${props => props.theme.fontWeights.Header3};
  line-height: ${props => props.theme.LineHeight.Header3};
  font-family: 'NotoSansKR';
  text-decoration: none;
  color: #000000;
  margin: auto 0;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap; 
  border: none;
  background: transparent;
  outline: none;
  width: 80%;
  cursor: ${props => props.readOnly ? 'default' : 'text'};
`;
const Pencil = styled.img`
  width: 38px;
  height: 38px;
  margin : auto 0;
  cursor: ${props => props.isadmin ? 'pointer' : 'default'};
`;

export default function Notification(){

  const [note, setNote] = useState("");
  const [isInput, setIsInput] = useState(false);
  const admin = localStorage.getItem("token");
  
  const isadmin = () =>{
  return (admin === "4/0AZEOvhXGPq3nOz7IdhK9Hq8V9fXkLlj6gMYSeurFcLGyTsshEgNbltxT-xLAfKw7QGVSkQ")//어드민 토큰과 같다면 클릭가능
  }
  const handleButtonClick = () => {
    if(isadmin()) {setIsInput(!isInput);}
  };

    return(
        <Div>
        <Img src={notification}></Img>
        {isadmin() ? (
          <>
        <Input
          type="text"
          value={note}
          onChange={(e)=> setNote(e.target.value)}
          readOnly={!isInput}
        />
        <Pencil src={pencil} onClick={handleButtonClick} isadmin={!isInput}></Pencil>
        </>
      ) : (
        <Input type="text" value={note} readOnly />
      )}
        </Div>
    )
}
