import React from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import { CKEditor } from "@ckeditor/ckeditor5-react";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";
import AWS from "aws-sdk";
import axios from "axios"; 
import modal from "react-modal";
import { main } from "@popperjs/core";


AWS.config.update({
  accessKeyId: process.env.REACT_APP_S3_ACCESS_KEY,
  secretAccessKey: process.env.REACT_APP_S3_SECRET_ACCESS_KEY,
  region: process.env.REACT_APP_S3_REGION,
});

const Div = styled.div`
  background: #f6f6f6;
  padding: 20px 40px 20px 40px;
  height: 100%;
`;
const Body3 = styled.div`
  font-size: ${(props) => props.theme.Web_fontSizes.Body3};
  font-weight: ${(props) => props.theme.fontWeights.Body3};
  line-height: ${(props) => props.theme.LineHeight.Body3};
  letter-spacing: -0.56px;
  font-style: normal;
  font-family: "NotoSansKR";
  color: var(--black, #000);
  text-align: center;
  margin-bottom: 11px;
`;
const Header2 = styled.h2`
  color: #000;
  font-family: "NotoSansKR";
  font-size: 32px;
  font-style: normal;
  font-weight: 700;
  line-height: 44px; /* 137.5% */
  letter-spacing: -1.8px;
`;
const RedAsterisk = styled.span`
  font-size: ${(props) => props.theme.Web_fontSizes.Body3};
  font-weight: ${(props) => props.theme.fontWeights.Body3};
  line-height: ${(props) => props.theme.LineHeight.Body3};
  letter-spacing: -0.56px;
  font-style: normal;
  font-family: "NotoSansKR";
  color: var(--black, #000);
  text-align: center;
  color: red;
`;

const Caption2 = styled.div`
  font-size: ${(props) => props.theme.Web_fontSizes.Caption2};
  font-weight: ${(props) => props.theme.fontWeights.Caption2};
  line-height: ${(props) => props.theme.LineHeight.Caption2};
  font-style: normal;
  font-family: "NotoSansKR";
  color: var(--admin-gray-disabled-button, #cfd4d9);
  text-align: center;
  margin-bottom: 30px;
`;

const RegisterDiv = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  margin-top: 10px;
  padding: 20px;
  gap: 15px;
  /* background-color: red; */
`;

const RegisterContentsDiv = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  /* background-color: green; */
`;

const RowDiv = styled.div`
  display: flex;
  flex-direction: row;
  width: 517px;
  gap: 40px;
  justify-content: flex-start;
  align-items: center;
`;

const Button = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  margin-top: 10px;
`;

const Cancel = styled.button`
  width: 153px;
  height: 42px;
  padding: 10px;
  margin-right: 30px;
  border-radius: 5px;
  border: 1px;
  gap: 10px;
  border-color: #E8E8E8;
`;

const Submit = styled.button`
  width: 153px;
  height: 42px;
  padding: 10px;
  border-radius: 5px;
  border: 1px;
  gap: 10px;
  background-color: #0079E0;
  color: #FFFFFF;
  border: none;
`;

const Title = styled.div`
  width: fit-content;
  white-space: nowrap;
`;

const TextInput = styled.input`
  width: 100%;
  height: 35px;
  border-radius: 2px;
  border: 1px solid #e8e8e8;
  padding: 2px 10px;
`;

const FileInput = styled.input`
  display: none; /* 숨김 처리 */
`;

const FileInputLabel = styled.label`
  width: 100px;
  padding: 5px;
  border-radius: 5px;
  border: 1px solid var(--admin-gray-stroke, #e8e8e8);
  background: var(--white, #fff);
  color: var(--black, #000);
  border-radius: 5px;
  text-align: center;
  cursor: pointer;
  font-size: ${(props) => props.theme.Web_fontSizes.Body3};
  font-weight: ${(props) => props.theme.fontWeights.Body3};
  line-height: ${(props) => props.theme.LineHeight.Body3};
`;

const TextEditor = styled.div`
  width: 100%;
  height: 350px;
  margin-top: 20px;
  overflow: auto;
`;

const FilePutDiv = styled.div`
  display: flex;
  /* background-color: blue; */
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const DropzoneDiv = styled.div`
  border-radius: 5px;
border: 1px solid var(--admin-gray-stroke, #E8E8E8);
background: var(--white, #FFF);
  width: 236px;
  height: 236px;
  margin: 10px;
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  cursor: pointer;
`;

const CategoryContainer = styled.div`
  display: flex;
  width: 100%;
  gap: 10px;
`;

const CategoryOption = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 85px;
  height: 35px;
  border-radius: 5px;
  background-color: ${(props) => (props.selected ? "#007BFF" : "#E8E8E8")};
  color: ${(props) => (props.selected ? "white" : "black")};
  cursor: pointer;
  transition: background-color 0.2s, color 0.2s;
  &:hover {
    background-color: ${(props) => (props.selected ? "#007BFF" : "#cfd4d9")};
  }
`;

const RadioContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

const RadioLabel = styled.label`
  margin-right: 10px;
`;

const RadioButton = styled.input`
  width: 18px;
  height: 18px;
`;

const SelectImg = styled.img`
  width: 100%;
  height: 100%;
`;

const ConfirmModal = styled(modal)`
  display: flex;
  flex-direction: column;
  width: 424px;
  height: 150px;
  border-radius: 20px;
  background-color: #fff;
  margin: 0 auto;
  padding: 25px;
`;

const ConfirmButton = styled.button`
  border: none;
  cursor: pointer;
  background-color: transparent;
`;


function ShopDataRegister() {
  const navigate = useNavigate();
  const [content, setContent] = useState("");
  const [selectedCategory, setSelectedCategory] = useState(""); 
  const [file, setFile] = useState();
  const [imageUrl, setImageUrl] = useState();
  const [imageView, setImageView] = useState();
  const [name, setName] = useState(""); 
  const [mainExposure, setMainExposure] = useState(false);


  const s3 = new AWS.S3();
  
  const handleConfirmClick = () => {
    navigate("/shopManager");
  };
  
  // const handleRegisterClick = async () => {
  //   const shouldRegister = window.confirm(
  //   "HGU 샵 등록하시겠습니까?\n등록 후 HGU 샵 목록에서 다시 설정 가능합니다."       
  //   );
  
  //   if (shouldRegister) {
  //     setIsModalOpen(true); // Open the modal
  //     try {
  //       // Upload the file to S3 and get the image URL
  //       await uploadToS3();
  
  //       if (!imageUrl) {
  //         alert("이미지 업로드 중입니다. 다시 한 번 등록하기 \n버튼을 눌러주세요.");
  //         return;
  //       }
        
  //       // Proceed with registration
  //       const data = {
  //         title : title,
  //         name: localStorage.getItem("userName"),
  //         image: imageUrl,
  //         content: content,
  //         category: selectedCategory,
  //       };
  
  //       const response = await axios.post(
  //         "http://54.180.86.22:8080/api/shops",
  //         data,
  //         {
  //           headers: { Authorization: localStorage.getItem("token") },
  //         }
  //       );
  
  //       console.log("API Response:", response.data);
  //       navigate("/shopManager");
  //     } catch (error) {
  //       alert("로그인이 필요합니다.");
  //       console.error("Error in handleRegisterClick:", error);
  //       throw error;
  //     }
  //   }
  // };
  

  const handleFileSelect = (e) => {
    var f = e.target.value;
    let fileLength = f.length;
    let fileDot = f.lastIndexOf(".");
    let fileType = f.substring(fileDot + 1, fileLength);
    if (fileType === "jpeg" || fileType === "jpg" || fileType === "png") {
      setFile(e.target.files[0]);
      setImageView(URL.createObjectURL(e.target.files[0]));
    } else {
      alert(".jpeg, .jpg, .png 파일만 업로드 가능합니다");
      setFile(null);
    }
  };

  const uploadToS3 = async () => {
    if (!file) {
      return;
    }
    const params = {
      Bucket: process.env.REACT_APP_S3_BUCKET,
      Key: "upload/" + `${Date.now()}.${file.name}`,
      Body: file,
    };
    const { Location } = await s3.upload(params).promise();
    setImageUrl(Location);
    console.log("uploading to s3", Location);
  };

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isRegisterConfirmOpen, setIsRegisterConfirmOpen] = useState(false);
  const [isCancelConfirmOpen, setIsCancelConfirmOpen] = useState(false);
  const [isConfirmOpen, setIsConfirmOpen] = useState(false);
  const [isSearchAlertOpen, setIsSearchAlertOpen] = useState(false);
  const [isOpen, setIsOpen] = useState(false);
  const [isCancelModalOpen, setIsCancelModalOpen] = useState(false);
  const [isNestedCancelModalOpen, setIsNestedCancelModalOpen] = useState(false);

  const openCancelModal = () => {
    setIsCancelModalOpen(true);
  };

  const closeCancelModal = () => {
    setIsCancelModalOpen(false);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };
  const openNestedCancelModal = () => {
    setIsNestedCancelModalOpen(true);
  };

  const closeNestedCancelModal = () => {
    setIsNestedCancelModalOpen(false);
  };

  const onRegister = async () => {
    try {
      await uploadToS3();
      if (!imageUrl) {
        alert("이미지 업로드 중입니다. 다시 한 번 등록하기 \n버튼을 눌러주세요.");
        return;
      }
  
      // Proceed with registration
      const data = {
        name: name,
        //name: localStorage.getItem("userName"),
        image: imageUrl,
        content: content,
        mainExposure: mainExposure,
        category: selectedCategory,
      };
      const response = await axios.post(
        `http://54.180.86.22:8080/api/shops`,
        data,
        {
          headers: { Authorization: localStorage.getItem("token") },
        }
      );
      console.log("API Response:", response.data);
      window.location.reload();
    } catch (error) {
      console.log("Error fetching notices:", error);
    }
    setIsRegisterConfirmOpen(false);
    setIsModalOpen(false);
  };

  // const handleModalConfirm = async () => {
  //   closeModal(); // Close the modal
  //   try {
  //     // Upload the file to S3 and get the image URL
  //     await uploadToS3();
  
  //     if (!imageUrl) {
  //       alert("이미지 업로드 중입니다. 다시 한 번 등록하기 \n버튼을 눌러주세요.");
  //       return;
  //     }
  
  //     // Proceed with registration
  //     const data = {
  //       title: title,
  //       name: localStorage.getItem("userName"),
  //       image: imageUrl,
  //       content: content,
  //       category: selectedCategory,
  //     };
  
  //     const response = await axios.post(
  //       "http://54.180.86.22:8080/api/shops",
  //       data,
  //       {
  //         headers: { Authorization: localStorage.getItem("token") },
  //       }
  //     );
  
  //     console.log("API Response:", response.data);
  //     navigate("/shopManager");
  //   } catch (error) {
  //     alert("로그인이 필요합니다.");
  //     console.error("Error in handleRegisterClick:", error);
  //     throw error;
  //   }
  // };
  
  return (
    <Div>
      <Header2>HGU 등록하기</Header2>
      <RegisterContentsDiv>
        <RegisterDiv>
          <RowDiv>
            <Title>제휴상품</Title>
            <TextInput
              type="text"
              placeholder="제목입력"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />          
            </RowDiv>
          <RowDiv>
            <Title>카테고리</Title>
            <CategoryContainer>
            {/* <CategoryOption
              selected={selectedCategory === "전체"} 
              onClick={() => setSelectedCategory("전체")}
            >
              전체
            </CategoryOption> */}
            <CategoryOption
              selected={selectedCategory === "식사"} 
              onClick={() => setSelectedCategory("식사")} 
            >
              식사
            </CategoryOption>
            <CategoryOption
              selected={selectedCategory === "카페"} 
              onClick={() => setSelectedCategory("카페")} 
            >
              카페
            </CategoryOption>
            <CategoryOption
              selected={selectedCategory === "피트니스"} 
              onClick={() => setSelectedCategory("피트니스")} 
            >피트니스</CategoryOption>
               <CategoryOption
              selected={selectedCategory === "미용"} 
              onClick={() => setSelectedCategory("미용")} 
            >미용</CategoryOption>
                <CategoryOption
              selected={selectedCategory === "기타"} 
              onClick={() => setSelectedCategory("기타")} 
            >기타</CategoryOption>
            </CategoryContainer>
          </RowDiv>
          <RowDiv>
            <Title>메인노출</Title>
            <RadioContainer>
              <RadioLabel>
                <RadioButton
            type="radio"
            name="mainExposure"
            value="Yes"
            checked={mainExposure === "Yes"}
            onChange={(e) => setMainExposure(e.target.value)}/>
                Yes
              </RadioLabel>
              <RadioLabel>
                <RadioButton type="radio"
      name="mainExposure"
      value="No"
      checked={mainExposure === "No"}
      onChange={(e) => setMainExposure(e.target.value)}/>
                No
              </RadioLabel>
            </RadioContainer>
          </RowDiv>
        </RegisterDiv>
        <FilePutDiv>
          <DropzoneDiv>
            {imageView ? (
              <SelectImg src={imageView} alt="uploaded" />
            ) : (
              <>
                <Body3>
                  썸네일 이미지 삽입 <RedAsterisk>*</RedAsterisk>
                </Body3>
                <Caption2>
                  드래그 업로드 또는 내 PC 검색
                  <br />
                  이미지 사이즈(250*250 권장)
                </Caption2>
                <FileInputLabel>
                  내 PC
                  <FileInput
                    type="file"
                    id="fileInput"
                    onChange={handleFileSelect}
                  />
                </FileInputLabel>
              </>
            )}
          </DropzoneDiv>
        </FilePutDiv>
      </RegisterContentsDiv>
      {/* ... */}
      <TextEditor>
        <CKEditor
          editor={ClassicEditor}
          data={content}
          onChange={(event, editor) => setContent(editor.getData())}
          onReady={(editor) => {
            console.log("Editor is ready to use!", editor);
          }}
          onBlur={(event, editor) => {
            console.log("Blur.", editor);
          }}
          onFocus={(event, editor) => {
            console.log("Focus.", editor);
          }}
          config={{
            placeholder: "내용을 입력하세요.",
          }}
        />
      </TextEditor>
      <Button>
        
        <Cancel onClick={() => setIsCancelConfirmOpen(true)}>취소</Cancel>
        <Submit onClick={() => setIsRegisterConfirmOpen(true)}>게시</Submit>
          <ConfirmModal
            isOpen={isRegisterConfirmOpen}
            onRequestClose={() => setIsRegisterConfirmOpen(false)}
            style={{ overlay: { backgroundColor: "rgba(0, 0, 0, 0.5)" } }}
            >
            <div style={{ fontWeight: "bold" }}>HGU 샵을 등록하시겠습니까?</div>
            <div>등록 후 HGU 샵 목록에서 다시 설정 가능합니다.</div>
            <Button style={{ justifyContent: "flex-end", marginTop: "24px" }}>
            <ConfirmButton onClick={openNestedCancelModal}>
              취소
            </ConfirmButton>
            <ConfirmButton onClick={onRegister}>확인</ConfirmButton>
            </Button>
          </ConfirmModal>
          <ConfirmModal
            isOpen={isNestedCancelModalOpen}
            onRequestClose={closeNestedCancelModal}
            style={{ overlay: { backgroundColor: "rgba(0, 0, 0, 0.5)" } }}
          >
          <div style={{ fontWeight: "bold" }}>HGU 등록을 취소하시겠습니까?</div>
          <div>취소 시 작성 중인 내용이 사라집니다. <br/> 그래도 취소하시겠습니까?</div>
          <Button style={{ justifyContent: "flex-end", marginBottom: "18px" }}>
            <ConfirmButton onClick={closeNestedCancelModal}>취소</ConfirmButton>
            <ConfirmButton onClick={handleConfirmClick}>확인</ConfirmButton>
        </Button>
      </ConfirmModal>
      </Button>
    </Div>
  );
}

export default ShopDataRegister;
