import React, { useState } from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown } from '@fortawesome/free-solid-svg-icons';

// 스타일드 컴포넌트로 드롭다운 스타일 정의
const DropdownContainer = styled.div`
  position: relative;
  display: inline-block;
`;

const DropdownButton = styled.button`
  display: flex;
  justify-content: space-between;
  align-items: center;
background: var(--neutral-1, #FFF);
  color: white;
  padding-top: 10px;
  padding-bottom: 10px;
  font-size: 16px;
  border: 1px solid var(--admin-gray-stroke, #E8E8E8);
  cursor: pointer;
  color: var(--character-title-85, rgba(0, 0, 0, 0.85));
  width: 100px;
  text-align: left;
  font-family: "NotoSansKR";
font-size: 14px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 19.6px */
letter-spacing: -0.56px;

`;
const ArrowIcon = styled.span`
  margin-left: 8px;
`;

const DropdownContent = styled.div`
  display: ${({ isOpen }) => (isOpen ? 'block' : 'none')};
  position: absolute;
  background-color: #f1f1f1;
  width: 90px;

  border-bottom: 1px;
  border-color: black;
  z-index: 1;
  text-align: left;
  font-family: "NotoSansKR";
font-size: 14px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 19.6px */
letter-spacing: -0.56px;
`;

const DropdownItem = styled.div`
  color: black;
  padding: 8px 0px 8px 10px;
  cursor: pointer;
  &:hover {
    background-color: #ddd;
  }
`;
const Dropdown = ({ options }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState(options[0]);
  
    const handleToggle = () => {
      setIsOpen(!isOpen);
    };
  
    const handleItemClick = (item) => {
      setSelectedOption(item); // 선택한 아이템으로 상태 업데이트
      setIsOpen(false); // 드롭다운 닫기
      sessionStorage.setItem("searchWordStatus",item);
    };
  
    return (
      <DropdownContainer>
        <DropdownButton onClick={handleToggle}>
          {selectedOption}
          <ArrowIcon>
          <FontAwesomeIcon icon={faChevronDown} />
        </ArrowIcon>
        </DropdownButton>
        <DropdownContent isOpen={isOpen}>
          {options.map((option, index) => (
            <DropdownItem key={index} onClick={() => handleItemClick(option)}>
              {option}
            </DropdownItem>
          ))}
        </DropdownContent>
      </DropdownContainer>
    );
  };
  
  export default Dropdown;
